import React, { useEffect, useContext, useState, useRef } from "react";
import Conversations from "../Component/conversations/Conversations";
import Message from "../Component/message/Message";
import AuthContext from "../context/auth";
import ChatAPI from "../Axios/ChatAPI";
import io from "socket.io-client";

const socket = io("http://localhost:5000", { transports: ["websocket"] });
const Chat = () => {
  const { user } = useContext(AuthContext);

  const [conversation, setConversation] = useState([]);
  const [currentChat, setCurrenchat] = useState("");
  const [message, setMessage] = useState([]);
  const [newmessage, setNewmessage] = useState("");
  const [load, setLoad] = useState(false);

  const onChangeText = (e) => {
    setNewmessage(e.target.value);
  };

  // handle post new mess
  const handleSubmit = async (e) => {
    const data = {
      message: newmessage,
      roomId: currentChat,
      is_admin: true,
    };

    await ChatAPI.postmess(data);
    setNewmessage("");
    setTimeout(() => {
      setLoad(true);
      socket.emit("send_message_server", data);
    }, 200);

    console.log(load);
  };
  // handle scroll
  // useEffect(() => {
  //   scrollRef.current?.scrollIntoView({ behavior: "smooth" });
  // }, [message]);

  // fetch Conver
  console.log(load);
  const fetchconver = async () => {
    const request = await ChatAPI.fecthConver();
    console.log(request);
    setConversation(request.chats);
  };
  // fetch mess

  const getMessages = async () => {
    const request = await ChatAPI.getmess(currentChat);
    setMessage(request.content);
    console.log("fetch mess");
  };

  // Hàm này dùng để load dữ liệu message của user khi user gửi tin nhán
  useEffect(() => {
    if (load) {
      //fetch mess
      getMessages();
      fetchconver();
      setLoad(false);
      console.log("reload");
    }
  }, [load]);

  useEffect(() => {
    setLoad(true);
  }, []);

  // Hàm này dùng để nhận socket từ server gửi lên
  useEffect(() => {
    //Nhận dữ liệu từ server gửi lên thông qua socket với key receive_message
    socket.on("receive_message_server", (data) => {
      console.log("receive_message", data);

      //Sau đó nó sẽ setLoad gọi lại hàm useEffect lấy lại dữ liệu
      setLoad(true);
    });
  }, []);

  useEffect(() => {
    socket.on("create room", (data) => {
      setLoad(true);
    });
    socket.on("end_room", (data) => {
      setLoad(true);
    });
  }, []);

  return (
    <div className="flex h-screen  basis-11/12 mt-5 ml-10 bg-white">
      <div className="basis-1/4  h-screen border border-r border-slate-100">
        <div className="p-5 h-screen">
          <input
            className="border border-slate-200 outline-none p-2"
            placeholder="Search Contact..."
          ></input>
          {conversation &&
            conversation.map((c) => {
              return (
                <div
                  onClick={() => {
                    setCurrenchat(c._id);
                    setLoad(true);
                  }}
                >
                  <Conversations key={c._id} data={c} currentUser={user} />
                </div>
              );
            })}
        </div>
      </div>
      <div className="basis-3/4  h-screen">
        <div className="p-5 h-screen flex flex-col">
          <div className="flex flex-col overflow-y-scroll scroll-smooth scrollbar">
            {message &&
              message.map((m) => {
                return (
                  <div
                    className={
                      !m.is_admin
                        ? "mt-4 flex justify-start pr-2"
                        : "mt-4 flex justify-end pr-2"
                    }
                  >
                    <div className="flex-col ">
                      {!m.is_admin && (
                        <img
                          className="w-8 h-8 rounded-full"
                          src="data:image/png;base64,"
                          alt=""
                        />
                      )}
                      <p
                        className={
                          m.is_admin
                            ? "p-2 rounded-3xl bg-teal-50  max-w-xs"
                            : "p-2 rounded-3xl bg-green-200  max-w-xs"
                        }
                      >
                        {m.message}
                      </p>
                    </div>
                  </div>
                );
              })}
          </div>
          <div className="flex justify-center space-x-4 mt-5 ">
            <textarea
              name=""
              id=""
              cols="30"
              rows="10"
              placeholder="write something"
              className="w-10/12 h-20 outline-none border border-slate-300"
              value={newmessage}
              onChange={onChangeText}
            ></textarea>
            <a
              className="
            h-10
            w-1/12
            cursor-pointer bg-black text-white "
              onClick={handleSubmit}
            >
              Send
            </a>
          </div>{" "}
        </div>
      </div>
    </div>
  );
};

export default Chat;
